mapper.select:
select

from t_file where is_deleted = 0 and uid in

    #{item}

select

from t_file where is_deleted = 0 and file_key in

    #{item}

mapper.update:
update t_file
set file_name = #{fileName}
where uid = #{fileId}
  and is_deleted = 0

update t_file
set file_state = #{fileState}
where file_key = #{fileKey}
and is_deleted = 0
